                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            package com.cts.jhpu.ui;

import javax.persistence.EntityManager;

import com.cts.jhpu.model.Address;
import com.cts.jhpu.model.Faculty;
import com.cts.jhpu.util.JPAUtil;

public class CompositionDemoEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Faculty f=new Faculty("Soumya",new Address("A210","Ocean Pearl" ,"Karnataka."));
		EntityManager em=JPAUtil.getEntityManagerFactory().createEntityManager();
		em.getTransaction().begin();
		em.persist(f);
		em.getTransaction().commit();
		System.out.println("Faculty Saved");
		JPAUtil.shutdown();
	}

}
